def slice_executor(file_number):
    # file_id = int. convert to Byte asap
    file_id = '{0:08b}'.format(file_number)

    with open('2-2-3_gadgets/data/slices/slice_'+file_id, 'r') as f:
        starting_value = int(file_id+f.readline(), 2)

    for k in range(CHUNKSIZE):
        input = starting_value + k
        input_b = '{0:035b}'.format(input)
        if input_validity(input_b):
            if problem_layer2(input_b)==False:
                with open('2-2-3_gadgets/data/save_to_look_at.txt', 'a') as f:
                    f.write(input_b+'\n')
    with open('2-2-3_gadgets/data/slices/slice_'+file_id, 'w') as f:
        f.write(input_b[8:])

 
def open_graphs():
    #return as a tuple the two adjacency matrices as layer1+2, complete
    with open(graph_only_layers, 'r') as layer3:
        M3 = recollect(layer3.readline())
    with open(graph_layered_2, 'r') as layer2:
        M2 = recollect(layer2.readline())
    return M3, M2

 
def problem_layer2(input):
    Nt = 48
    X = [Int("x%s" %(i)) for i in range(Nt)]
    bools = [And(X[i]<=1, X[i]>=0) for i in range(Nt)]
    const1 = [(sum(layer2_m[i])-2)*(1-X[i]) <= sum([X[j] for j in range(Nt) if layer2_m[i][j] == 1]) for i in range(Nt)]  #if color is 0 then I have at least 3 neighbors of col 1
    const2 = [(sum(layer2_m[i])-2)*X[i] <= sum([(1-X[j]) for j in range(Nt) if layer2_m[i][j]==1]) for i in range(Nt)]  #if color is 1 then I have at least 3 neighbors of col 0
    init = [X[12+i] == int(input[i]) for i in range(len(input))]
    s = Solver()
    s.add(bools+const1+const2+init)
    if s.check() == sat:
        print("TRUE")
        return True
    else:
        print("FALSE")
        return False



 

def problem_layer3(input):
    #only taking in account the two layers, check if a problem is feasible. If it is return True.
    #input = binary input corresponding to layer input values; string.
    Nt = 48
    
    X = [Int("x%s" %(i)) for i in range(Nt)]
    bools = [And(X[i]<=1, X[i]>=0) for i in range(Nt)] 
    const1 = [(sum(layer3_m[i])-2)*(1-X[i]) <= sum([layer3_m[i][j]*X[j] for j in range(Nt-1)]) for i in range(Nt-1)]  #if color is 0 then I have at least 3 neighbors of col 1
    const2 = [(sum(layer3_m[i])-2)*X[i] <= sum([layer3_m[i][j]*(1-X[j]) for j in range(Nt-1)]) for i in range(Nt-1)]  #if color is 1 then I have at least 3 neighbors of col 0

    init = [X[12+i] == int(input[i]) for i in range(len(input))]
    s=Solver()
    s.add(bools+const1+const2+init)
    if s.check()==sat:
        return True
    else: 
        return False

 
